import React, { useState } from 'react';
import './RoomBooking.css';  // Assuming this CSS file includes necessary styles

function PricingAndInvoice() {
    const [invoiceGenerated, setInvoiceGenerated] = useState(false);
    
    // Ensure you have 'bookingDate' stored in localStorage or set a default value
    const bookingDetails = {
        place: "Mountain Lodge",
        roomType: "Suite",
        date: localStorage.getItem('bookingDate') || 'Date not set',
        price: 250  // Assuming $250 as an example price per night
    };

    const handleGenerateInvoice = () => {
        setInvoiceGenerated(true);
    };

    const handleDownloadInvoice = () => {
        // In a real application, you would trigger a file download here
        alert("Downloading invoice... (simulate download in a real application)");
    };

    const handleRequestBooking = () => {
        alert("Thank you for booking. We will get in touch with you soon!");
        // Redirect to the homepage after booking
        window.location.href = '/';  // Ensure your routing is configured correctly for this to work
    };

    return (
        <div className="room-booking-container">
            <h1>Pricing and Invoice</h1>
            <div className="form-section">
                <p><strong>Place:</strong> {bookingDetails.place}</p>
                <p><strong>Room Type:</strong> {bookingDetails.roomType}</p>
                <p><strong>Date:</strong> {bookingDetails.date}</p>
                <p><strong>Price per night:</strong> ${bookingDetails.price}</p>
                {invoiceGenerated ? (
                    <div className="invoice">
                        <h2>Invoice</h2>
                        <p>Thank you for choosing {bookingDetails.place}.</p>
                        <p><strong>Total Cost:</strong> ${bookingDetails.price}</p>
                        <button onClick={handleDownloadInvoice}>Download Invoice</button>
                        <button onClick={handleRequestBooking}>Request for Booking</button>
                    </div>
                ) : (
                    <button onClick={handleGenerateInvoice}>Generate Invoice</button>
                )}
            </div>
        </div>
    );
}

export default PricingAndInvoice;
