import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function RideBookingConfirmation() {
    const navigate = useNavigate();
    const [bookingDetails, setBookingDetails] = useState({});
    const [loading, setLoading] = useState(true);
    const userEmail = localStorage.getItem('userEmail'); // Retrieve email from local storage

    useEffect(() => {
        async function fetchBookingDetails() {
            try {
                // Use userEmail in your API request if needed
                const response = await fetch(`http://localhost:5000/rideDetailsByEmail/${userEmail}`);
                if (!response.ok) throw new Error('Failed to fetch');
                const data = await response.json();
                setBookingDetails(data);
                setLoading(false);
            } catch (error) {
                console.error('Fetch error:', error);
                setLoading(false);
            }
        }
        fetchBookingDetails();
    }, [userEmail]); // Dependency on userEmail

    if (loading) return <p>Loading...</p>;

    return (
        <div>
            <h1>Ride Booking Confirmation</h1>
            <p>Email: {userEmail}</p> {/* Display the email */}
            <p>Pickup Location: {bookingDetails.pickup_location}</p>
            <p>Destination: {bookingDetails.drop_location}</p>
            <p>Date and Time: {bookingDetails.datetime}</p>
            <p>Driver Name: John Doe</p>
            <p>Driver Name: John Doe</p>
            <p>Driver Mobile No.: 123-456-7890</p>
            <p>Amount Paid: ${bookingDetails.total_cost}</p>
            <p>Your receipt will be sent to your email after the completion of the ride. Have a safe journey!</p>
            {/* Implement Download Invoice and Return to Home Button here */}
        </div>
    );
}

export default RideBookingConfirmation;
