import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlane, faBed, faCar } from '@fortawesome/free-solid-svg-icons';

function TourSelection() {
  const navigate = useNavigate();
  const destinations = [
    { name: "Indonesia", image: "https://example.com/indonesia.jpg", path: "/book-indonesia" },
    { name: "Switzerland", image: "https://example.com/switzerland.jpg", path: "/book-switzerland" },
    { name: "Iceland", image: "https://example.com/iceland.jpg", path: "/book-iceland" },
    { name: "New Zealand", image: "https://example.com/newzealand.jpg", path: "/book-newzealand" },
    { name: "Norway", image: "https://example.com/norway.jpg", path: "/book-norway" },
    { name: "Italy", image: "https://example.com/italy.jpg", path: "/book-italy" },
    { name: "Japan", image: "https://example.com/japan.jpg", path: "/book-japan" },
    { name: "Vietnam", image: "https://example.com/vietnam.jpg", path: "/book-vietnam" },
    { name: "Thailand", image: "https://example.com/thailand.jpg", path: "/book-thailand" },
  ];

  const handleNavigate = (path) => {
    navigate(path);
  };

  const styles = {
    page: {
      fontFamily: 'Roboto, sans-serif',
      color: '#333',
    },
    header: {
      background: 'linear-gradient(135deg, rgba(195, 74, 74, 0.701), rgba(52, 147, 166, 0.652))',
      padding: '1rem 2rem',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      position: 'fixed',
      width: '100%',
      top: 0,
      zIndex: 1000,
    },
    logo: {
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#fff',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '1rem',
      padding: '2rem',
      marginTop: '60px',
    },
    card: {
      background: '#fff',
      border: '1px solid #ddd',
      borderRadius: '8px',
      overflow: 'hidden',
      cursor: 'pointer',
      transition: 'transform 0.3s ease',
    },
    image: {
      height: '200px',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
    },
    button: {
      background: '#4CAF50',
      color: 'white',
      border: 'none',
      padding: '10px 20px',
      width: '100%',
      display: 'block',
      textAlign: 'center',
      fontSize: '1rem',
    }
  };

  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <div style={styles.logo}>TravelMate</div>
      </header>
      <div style={styles.grid}>
        {destinations.map((dest, index) => (
          <div key={index} style={styles.card} onClick={() => handleNavigate(dest.path)}>
            <div style={{ ...styles.image, backgroundImage: `url(${dest.image})` }}></div>
            <div>
              <h3>{dest.name}</h3>
              <button style={styles.button}>Book Now</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TourSelection;
