import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password })
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        navigate('/landing');
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert('Login failed. Please try again later.');
    }
  };

  // Same style object as used in Signup
  const style = {
    container: {
      fontFamily: '"Comic Sans MS", cursive, sans-serif',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      backgroundImage: 'url(https://images.unsplash.com/photo-1506748686214-e9df14d4d9d0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1950&q=80)',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      textAlign: 'center',
      color: 'black'
    },
    form: {
      background: 'rgba(255, 255, 255, 0.9)',
      padding: '40px',
      borderRadius: '15px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      width: '90%',
      maxWidth: '400px'
    },
    input: {
      padding: '12px 20px',
      margin: '8px 0',
      width: '100%',
      borderRadius: '5px',
      border: '1px solid #ccc'
    },
    button: {
      backgroundColor: '#28A745',
      color: 'white',
      padding: '12px 20px',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      width: '100%',
      fontSize: '16px',
      marginTop: '20px'
    }
  };

  return (
    <div style={style.container}>
      <div style={style.form}>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input style={style.input} type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter your email" required />
          <input style={style.input} type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Enter your password" required />
          <button style={style.button} type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
