import React from 'react';
import { useNavigate } from 'react-router-dom';

function TourSelection() {
  const navigate = useNavigate();
  const destinations = [
    { name: "Indonesia", image: "https://images.unsplash.com/photo-1587019157285-20b1e5dd3116", path: "/book-destination" },
    { name: "Switzerland", image: "https://images.unsplash.com/photo-1587149939632-fa5c8c529c9b", path: "/book-destination" },
    { name: "Iceland", image: "https://images.unsplash.com/photo-1600699899978-1f0a56921498", path: "/book-destination" },
    { name: "New Zealand", image: "https://images.unsplash.com/photo-1587613990441-f3c4e886d99a", path: "/book-destination" },
    { name: "Norway", image: "https://images.unsplash.com/photo-1507668077129-56e32842fceb", path: "/book-destination" },
    { name: "Italy", image: "https://images.unsplash.com/photo-1598970434795-0c54fe7c0647", path: "/book-destination" },
    { name: "Japan", image: "https://images.unsplash.com/photo-1551884170-f7b49983e8b2", path: "/book-destination" },
    { name: "Vietnam", image: "https://images.unsplash.com/photo-1564325724739-bae0bd08762c", path: "/book-destination" },
    { name: "Thailand", image: "https://images.unsplash.com/photo-1552925686-e8afeb4f6754", path: "/book-destination" },
    { name: "Dubai", image: "https://images.unsplash.com/photo-1512453979798-5ea266f8880c", path: "/book-destination" }
  ];

  const handleNavigate = (path) => {
    navigate(path);
  };

  const styles = {
    page: {
      fontFamily: '"Brush Script MT", cursive', // Updated to cursive font
      color: '#333',
      backgroundImage: 'url("https://images.unsplash.com/photo-1507525428034-b723cf961d3e")', // Global background for the page
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      height: '100vh',
      overflow: 'auto'
    },
    header: {
      padding: '1rem 2rem',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      position: 'fixed',
      width: '100%',
      top: 0,
      zIndex: 1000,
      backgroundColor: 'rgba(0, 0, 0, 0.7)', // Ensures readability
    },
    logo: {
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#fff',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '1rem',
      padding: '2rem',
      paddingTop: '60px', // Adjusted to avoid hiding under the fixed header
    },
    card: {
      cursor: 'pointer',
      transition: 'transform 0.3s ease',
      boxShadow: '0 4px 6px rgba(0,0,0,0.1)', // Adds subtle shadows for depth
    },
    image: {
      height: '200px',
      width: '100%', // Ensures images cover the card
      objectFit: 'cover', // Ensures images are nicely fitted
      borderRadius: '8px 8px 0 0', // Rounds only the top corners
    },
    text: {
      padding: '10px',
      textAlign: 'center', // Centers text in the card
      fontSize: '1.2rem',
    }
  };

  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <div style={styles.logo}>TravelMate</div>
      </header>
      <div style={styles.grid}>
        {destinations.map((dest, index) => (
          <div key={index} style={styles.card} onClick={() => handleNavigate(dest.path)}>
            <img src={dest.image} alt={dest.name} style={styles.image} />
            <div style={styles.text}>{dest.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TourSelection;
