import React, { useState } from 'react';

function RideBooking() {
    const [pickupLocation, setPickupLocation] = useState('');
    const [destination, setDestination] = useState('');
    const [dateTime, setDateTime] = useState('');
    const [driverInfo, setDriverInfo] = useState(null);
    const [loading, setLoading] = useState(false);
    const [rideCost, setRideCost] = useState(null);
    const [paymentComplete, setPaymentComplete] = useState(false);
    const [showPrices, setShowPrices] = useState(false);

    const handleNext = () => {
        if (!pickupLocation || !destination || !dateTime) {
            alert('Please fill in all fields');
            return;
        }
        setLoading(true);
        setTimeout(() => {
            setLoading(false);
            alert('Driver available');
            setDriverInfo({
                name: 'John Doe',
                number: '123-456-7890',
                vehicleType: 'Sedan'
            });
        }, 5000);
    };

    const handleAccept = () => {
        // Generate a random ride cost between $20 and $50
        const baseCost = Math.floor(Math.random() * (50 - 20 + 1) + 20);
        const serviceCharge = 5; // Fixed service charge
        const tax = baseCost * 0.1; // 10% tax
        const totalCost = baseCost + serviceCharge + tax;
        setRideCost({
            baseCost,
            serviceCharge,
            tax,
            totalCost
        });
        setShowPrices(true);
    };

    const handlePayment = () => {
        setPaymentComplete(true);
        alert('Payment has been made successfully.');
        setTimeout(() => {
            window.location.href = '/nextPage';
        }, 2000);
    };

    return (
        <div>
            <h1>Book a Ride</h1>
            <div>
                <p>Where do you want to travel?</p>
                <input
                    type="text"
                    placeholder="Pickup Location"
                    value={pickupLocation}
                    onChange={e => setPickupLocation(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Destination"
                    value={destination}
                    onChange={e => setDestination(e.target.value)}
                />
                <input
                    type="datetime-local"
                    value={dateTime}
                    onChange={e => setDateTime(e.target.value)}
                />
                <button onClick={handleNext}>{'Next'}</button>
            </div>
            {driverInfo && !loading && (
                <div>
                    <h2>Driver Details</h2>
                    <p>Name: {driverInfo.name}</p>
                    <p>Number: {driverInfo.number}</p>
                    <p>Vehicle Type: {driverInfo.vehicleType}</p>
                    <button onClick={handleAccept}>Accept</button>
                    {showPrices && rideCost && (
                        <div>
                            <p>Base Fare: ${rideCost.baseCost.toFixed(2)}</p>
                            <p>Service Charge: ${rideCost.serviceCharge.toFixed(2)}</p>
                            <p>Tax: ${rideCost.tax.toFixed(2)}</p>
                            <p>Total Cost: ${rideCost.totalCost.toFixed(2)}</p>
                            {paymentComplete ? (
                                <p>Payment complete!</p>
                            ) : (
                                <button onClick={handlePayment}>Pay ${rideCost.totalCost.toFixed(2)}</button>
                            )}
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default RideBooking;
