import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlane, faBed, faCar } from '@fortawesome/free-solid-svg-icons';

function LandingPage() {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      navigate(path);
    }, 2000); // Simulate loading for 2 seconds
  };

  return (
    <div className="landing-page">
      <header className="header">
        <a href="/" className="logo-link">
          <div className="logo"><FontAwesomeIcon icon={faPlane} /> TravelMate</div>
        </a>
      </header>

      <section className="hero">
        <div className="hero-content">
          <h1>Explore the World with Us</h1>
          <p>Your adventure starts here. Book tours, secure accommodations, and explore local attractions.</p>
          <div className="cta-buttons">
            <button onClick={() => handleNavigation('/tour-booking')} className="btn btn-tour"><FontAwesomeIcon icon={faPlane} /> Book a Tour</button>
            <button onClick={() => handleNavigation('/room-booking')} className="btn btn-room"><FontAwesomeIcon icon={faBed} /> Book a Room</button>
            <button onClick={() => handleNavigation('/ride-booking')} className="btn btn-ride"><FontAwesomeIcon icon={faCar} /> Book a Ride</button>
          </div>
        </div>
      </section>
      
      {loading && <div className="loading-overlay">Loading...</div>}
    </div>
  );
}

export default LandingPage;
