import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function RoomBooking() {
    const [rooms, setRooms] = useState([]);
    const [selectedRoom, setSelectedRoom] = useState(null);
    const [bookingDetails, setBookingDetails] = useState({ name: '', contact: '', date: new Date() });

    // Fetch available rooms when the component mounts
    useEffect(() => {
        const fetchRooms = async () => {
            const response = await fetch('/api/rooms'); // Adjust endpoint as necessary
            const data = await response.json();
            setRooms(data);
        };

        fetchRooms();
    }, []);

    const handleRoomSelect = (room) => {
        setSelectedRoom(room);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setBookingDetails(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const bookingData = { ...bookingDetails, roomId: selectedRoom.id };
        const response = await fetch('/api/bookRoom', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(bookingData)
        });
        const result = await response.json();
        alert(result.message); // Display booking confirmation or error message
    };

    return (
        <div>
            <h1>Room Booking</h1>
            <h2>Select a Room</h2>
            {rooms.map(room => (
                <div key={room.id} onClick={() => handleRoomSelect(room)}>
                    <h3>{room.type} - ${room.price}</h3>
                </div>
            ))}

            {selectedRoom && (
                <form onSubmit={handleSubmit}>
                    <label>
                        Name:
                        <input type="text" name="name" value={bookingDetails.name} onChange={handleChange} required />
                    </label>
                    <label>
                        Contact:
                        <input type="text" name="contact" value={bookingDetails.contact} onChange={handleChange} required />
                    </label>
                    <label>
                        Date:
                        <DatePicker selected={bookingDetails.date} onChange={date => setBookingDetails({...bookingDetails, date})} />
                    </label>
                    <button type="submit">Book Room</button>
                </form>
            )}
        </div>
    );
}

export default RoomBooking;
