import React, { useState, useEffect } from 'react';
import './TourBooking.css'; // Assuming you have a CSS file for styles

const TourBooking = () => {
    const [tours, setTours] = useState([]);
    const [selectedTour, setSelectedTour] = useState('');
    const [availableSlots, setAvailableSlots] = useState([]);
    const [selectedSlot, setSelectedSlot] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [contact, setContact] = useState('');
    const [participants, setParticipants] = useState(1);
    const [message, setMessage] = useState('');
    const [responseStatus, setResponseStatus] = useState(null);
    const [destinations, setDestinations] = useState([]);
    const [selectedDestination, setSelectedDestination] = useState('');

    useEffect(() => {
        const fetchTours = async () => {
            const response = await fetch('/api/tours'); // Adjust API endpoint accordingly
            const data = await response.json();
            setTours(data);
            // Optionally set destinations if relevant
            setDestinations([...new Set(data.map(tour => tour.destination))]); // Unique destinations
        };
        fetchTours();
    }, []);

    const handleTourChange = async (e) => {
        const tourId = e.target.value;
        setSelectedTour(tourId);
        setSelectedSlot('');

        if (tourId) {
            const response = await fetch(`/api/tours/${tourId}/slots`);
            const slots = await response.json();
            setAvailableSlots(slots);
        } else {
            setAvailableSlots([]);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const bookingData = { name, email, contact, selectedSlot, participants, destination: selectedDestination };

        const response = await fetch('/api/bookTour', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(bookingData),
        });

        if (response.ok) {
            setMessage('Booking successful!');
            setResponseStatus('success');
            setName('');
            setEmail('');
            setContact('');
            setSelectedSlot('');
            setParticipants(1);
            setAvailableSlots([]);
            setSelectedTour('');
            setSelectedDestination('');
        } else {
            setMessage('Booking failed. Please try again.');
            setResponseStatus('error');
        }
    };

    return (
        <div className="tour-booking">
            <h2>Book a Tour</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Select Destination:
                    <select value={selectedDestination} onChange={(e) => setSelectedDestination(e.target.value)} required>
                        <option value="">Select a destination</option>
                        <option value="">rai</option>
                        {destinations.map(destination => (
                            <option key={destination} value={destination}>{destination}</option>
                            
                        ))}
                    </select>
                </label>

                {availableSlots.length > 0 && (
                    <label>
                        Available Slots:
                        <select value={selectedSlot} onChange={(e) => setSelectedSlot(e.target.value)} required>
                            <option value="">Select a slot</option>
                            {availableSlots.map(slot => (
                                <option key={slot.id} value={slot.time}>{slot.time}</option>
                            ))}
                        </select>
                    </label>
                )}

                <label>
                    Name:
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
                </label>

                <label>
                    Email:
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </label>

                <label>
                    Contact Number:
                    <input type="tel" value={contact} onChange={(e) => setContact(e.target.value)} required />
                </label>

                <label>
                    Number of People:
                    <input type="number" value={participants} onChange={(e) => setParticipants(e.target.value)} min="1" required />
                </label>

                <button type="submit">Book Now</button>
            </form>

            {message && (
                <p className={responseStatus === 'success' ? "message success" : "message error"}>
                    {message}
                </p>
            )}
        </div>
    );
};

export default TourBooking;
