import React from 'react';
import { useNavigate } from 'react-router-dom';

function RideBookingConfirmation({ bookingDetails }) {
  const navigate = useNavigate();

  const handleDownloadInvoice = () => {
    const invoiceData = `Receipt\n\nRide Details:\nPickup: ${bookingDetails.pickup}\nDestination: ${bookingDetails.destination}\nDate and Time: ${bookingDetails.dateTime}\nDriver: ${bookingDetails.driverName}\nAmount Paid: $${bookingDetails.totalCost}\n\nYour receipt will be sent to your email after the completion of the ride. Have a safe journey!`;
    const blob = new Blob([invoiceData], { type: 'text/plain' });
    const href = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = href;
    link.download = "RideInvoice.txt"; // Naming the download file
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleReturnHome = () => {
    navigate('/'); // Assuming your home route is "/"
  };

  return (
    <div>
      <h1>Ride Booking Confirmation</h1>
      <p>Pickup Location: {bookingDetails.pickup}</p>
      <p>Destination: {bookingDetails.destination}</p>
      <p>Date and Time: {bookingDetails.dateTime}</p>
      <p>Driver Name: {bookingDetails.driverName}</p>
      <p>Amount Paid: ${bookingDetails.totalCost}</p>
      <p>Your receipt will be sent to your email after the completion of the ride. Have a safe journey!</p>
      <button onClick={handleDownloadInvoice}>Download Invoice</button>
      <button onClick={handleReturnHome}>Return to Home</button>
    </div>
  );
}

export default RideBookingConfirmation;
