import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';



function TourConfirmation() {
  const navigate = useNavigate();
  const [details, setDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const crypto = require('crypto');

    // Function to encrypt text
    function encrypt(text) {
        const algorithm = 'aes-256-cbc';
        // Ensure the key and IV are environment variables and not hard-coded
        const key = Buffer.from(process.env.ENCRYPTION_KEY, 'hex'); // Key should be 32 bytes (256 bits)
        const iv = crypto.randomBytes(16); // Generates a new IV for each encryption

        const cipher = crypto.createCipheriv(algorithm, key, iv);
        let encrypted = cipher.update(text, 'utf8', 'hex');
        encrypted += cipher.final('hex');

        return {
            iv: iv.toString('hex'), // Return IV with the encrypted data because it's needed for decryption
            encryptedData: encrypted
        };
    }

  
  useEffect(() => {
    const fetchData = async () => {
      try {
        const email = localStorage.getItem('email'); // Assuming user ID is stored in localStorage
        const response = await fetch(`http://localhost:5000/api/payment-details/${encrypt(email)}`);
        if (!response.ok) {
          throw new Error('Failed to fetch details');
        }
        const data = await response.json();
        setDetails(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleNavigateBack = () => {
    navigate('/'); // Adjust the navigation route as needed
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Thank You for Your Booking!</h1>
      <p>We will soon be in touch with you regarding your trip details.</p>
      {details && (
        <div>
          <p><strong>Email:</strong> {details.email}</p>
          <p><strong>Name:</strong> {details.name}</p>
          <p><strong>Contact:</strong> {details.contact}</p>
          <p><strong>Package Selected:</strong> {details.package_selected}</p>
          <p><strong>Number of People:</strong> {details.number_of_people}</p>
          <p><strong>Total Cost:</strong> ${details.total_cost.toFixed(2)}</p>
          <p><strong>Payment Status:</strong> {details.payment_status}</p>
        </div>
      )}
      <footer>
        <p>If you have any questions, please contact us:</p>
        <p>ðŸ“ž XXXXXXXXXX</p>
        <p>ðŸ“§ XYZ@travelmate.com</p>
      </footer>
      <button onClick={handleNavigateBack}>Return to Home</button>
    </div>
  );
}

export default TourConfirmation;
