import React, { useState } from 'react';
import './TourBooking.css';

const TourBooking = () => {
    const [destinations] = useState([
        'Sydney', 'Melbourne', 'Thailand', 'Japan', 
        'Switzerland', 'Maldives', 'Iceland', 
        'Germany', 'Spain', 'New Zealand'
    ]);
    const [selectedDestination, setSelectedDestination] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [participants, setParticipants] = useState(1);
    const [message, setMessage] = useState('');
    const [responseStatus, setResponseStatus] = useState(null);
    const [selectedDate, setSelectedDate] = useState('');

    // Mock availability check (could later be replaced with an actual API request)
    const checkAvailability = (destination, date) => {
        // Mock condition: Assume 'Iceland' is not available
        return !(destination === 'Iceland' && new Date(date) < new Date());
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!checkAvailability(selectedDestination, selectedDate)) {
            setMessage('Selected destination is not available on the chosen date.');
            setResponseStatus('error');
            return;
        }

        const bookingData = { name, email, participants, selectedDestination, selectedDate };
        
        try {
            const response = await fetch('http://localhost:5000/api/bookTour', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(bookingData),
            });

            if (response.ok) {
                setMessage('Booking successful!');
                setResponseStatus('success');
                // Clear form after successful booking
                setName('');
                setEmail('');
                setParticipants(1);
                setSelectedDestination('');
                setSelectedDate('');
            } else {
                throw new Error('Failed to book the tour');
            }
        } catch (error) {
            setMessage('Booking failed. Please try again.');
            setResponseStatus('error');
        }
    };

    return (
        <div className="tour-booking">
            <h2>Book a Tour</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Select Destination:
                    <select value={selectedDestination} onChange={(e) => setSelectedDestination(e.target.value)} required>
                        <option value="">Select a destination</option>
                        {destinations.map(destination => (
                            <option key={destination} value={destination}>{destination}</option>
                        ))}
                    </select>
                </label>

                <label>
                    Select Date:
                    <input 
                        type="date" 
                        value={selectedDate} 
                        onChange={(e) => setSelectedDate(e.target.value)} 
                        required 
                    />
                </label>

                <label>
                    Name:
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
                </label>

                <label>
                    Email:
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </label>

                <label>
                    Number of Participants:
                    <input type="number" value={participants} onChange={(e) => setParticipants(e.target.value)} min="1" required />
                </label>

                <button class="submitButton" type="submit">Book Now</button>
            </form>

            {message && (
                <p className={responseStatus === 'success' ? "message success" : "message error"}>
                    {message}
                </p>
            )}
        </div>
    );
};

export default TourBooking;
