import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styles from './RideBookingConfirmation.module.css';

function RideBookingConfirmation() {
    const { bookingId } = useParams(); // Assuming you're passing the booking ID in the URL
    const navigate = useNavigate();
    const [bookingDetails, setBookingDetails] = useState({});
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchBookingDetails() {
            try {
                const response = await fetch(`/api/rideDetails/${bookingId}`);
                if (!response.ok) throw new Error('Failed to fetch');
                const data = await response.json();
                setBookingDetails(data);
                setLoading(false);
            } catch (error) {
                console.error('Fetch error:', error);
                setLoading(false);
            }
        }
        fetchBookingDetails();
    }, [bookingId]);

    if (loading) return <p>Loading...</p>;

    return (
        <div>
            <h1>Ride Booking Confirmation</h1>
            <p>Pickup Location: {bookingDetails.pickup_location}</p>
            <p>Destination: {bookingDetails.drop_location}</p>
            <p>Date and Time: {bookingDetails.datetime}</p>
            <p>Driver Name: {bookingDetails.driver_name}</p> {/* Adjust based on your database schema */}
            <p>Amount Paid: ${bookingDetails.total_cost}</p>
            <p>Your receipt will be sent to your email after the completion of the ride. Have a safe journey!</p>
            {/* Implement Download Invoice and Return to Home Button here */}
        </div>
    );
}

export default RideBookingConfirmation;
