import React, { useState } from 'react';
import './LandingPage.css';

function LandingPage() {
    const [username, setUsername] = useState('');
    const [service, setService] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!username || !service) {
            alert('Please enter your username and select a service.');
            return;
        }
        
        alert(`Username: ${username}, Service: ${service}`);
        
        // Based on the selected service, you can navigate to another route or call an API
        // Example: if service === 'tour', you can redirect or call the API for the tour service
        // Example: window.location.href = `/tour-booking?username=${username}`; // Use React Router for dynamic routing instead
    };

    return (
        <div className="container">
            <h1>Tourism Microservices</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Enter Username:
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </label>

                <label>
                    Select Service:
                    <div>
                        <input
                            type="radio"
                            id="tour"
                            name="service"
                            value="tour"
                            onChange={(e) => setService(e.target.value)}
                            required
                        />
                        <label htmlFor="tour">Tour Booking</label>
                    </div>
                    <div>
                        <input
                            type="radio"
                            id="ticket"
                            name="service"
                            value="ticket"
                            onChange={(e) => setService(e.target.value)}
                            required
                        />
                        <label htmlFor="ticket">Ticket Reservation</label>
                    </div>
                    <div>
                        <input
                            type="radio"
                            id="ride"
                            name="service"
                            value="ride"
                            onChange={(e) => setService(e.target.value)}
                            required
                        />
                        <label htmlFor="ride">Ride Booking</label>
                    </div>
                </label>

                <button type="submit">Proceed</button>
            </form>
        </div>
    );
}

export default LandingPage;
