import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Payment.css'; // Import the CSS file
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlane } from '@fortawesome/free-solid-svg-icons';

function Payment() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false); 
  const [formData, setFormData] = useState({
    name: '',
    email: localStorage.getItem('userEmail') || '',
    contact: '',
    numberOfPeople: 1,
    totalCost: 2500.00
  });
  const [paymentProcessed, setPaymentProcessed] = useState(false); // Track if payment was processed

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "numberOfPeople") {
      const totalCost = Math.min(value, 10) * 2500;
      setFormData({ ...formData, numberOfPeople: value, totalCost });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handlePayment = () => {
    setLoading(true); // Simulate loading/payment processing
    setTimeout(() => {
      setLoading(false);
      setPaymentProcessed(true); // Simulate successful payment
      alert('Payment processed successfully. Please confirm your booking.');
    }, 2000); // Simulate payment processing delay
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!paymentProcessed) {
      alert('Please process the payment before confirming your booking.');
      return;
    }
    setLoading(true); // Start loading
    try {
      const response = await fetch('/payment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          email: localStorage.getItem('userEmail'),
          packageSelected: localStorage.getItem('selectedPackage'),
          paymentStatus: 'Paid' // Assume payment is always successful for demo
        })
      });
      if (response.ok) {
        setTimeout(() => {
          setLoading(false);
          navigate('/confirmation');
        }, 2000);
      } else {
        const errorData = await response.json();
        setLoading(false);
        alert('Payment failed. Please try again. ' + errorData.message);
      }
    } catch (error) {
      setLoading(false);
      console.error('Payment submission error:', error);
      alert('Error processing payment.');
    }
  };

  return (
    <div className="app-container">
      <header className="header">
        <a href="/" className="logo-link">
          <div className="logo"><FontAwesomeIcon icon={faPlane} /> TravelMate</div>
        </a>
      </header>
      {loading && (
        <div className="loading-overlay">
          Loading...
        </div>
      )}
      <div className="payment-container">
        <form onSubmit={handleSubmit} className="payment-form">
          <h1>Payment Details</h1>
          <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Your Name" required />
          <input type="email" name="email" value={formData.email} readOnly />
          <input type="text" name="contact" value={formData.contact} onChange={handleChange} placeholder="Your Contact Number" required />
          <select name="numberOfPeople" value={formData.numberOfPeople} onChange={handleChange}>
            {[...Array(10)].map((_, index) => (
              <option key={index} value={index + 1}>{index + 1}</option>
            ))}
          </select>
          <p>Total Cost: ${formData.totalCost.toFixed(2)}</p>
          <button type="button" onClick={handlePayment} disabled={paymentProcessed}>
            Pay
          </button>
          <button type="submit" disabled={!paymentProcessed}>
            Confirm Payment
          </button>
        </form>
      </div>
    </div>
  );
}

export default Payment;
