import React from 'react';
import { useNavigate } from 'react-router-dom';

function TourSelection() {
  const navigate = useNavigate();
  const destinations = [
    { name: "Indonesia", image: "https://images.unsplash.com/photo-1587019157285-20b1e5dd3116", path: "/book-destination" },
    { name: "Switzerland", image: "https://images.unsplash.com/photo-1587149939632-fa5c8c529c9b", path: "/book-destination" },
    { name: "Iceland", image: "https://images.unsplash.com/photo-1600699899978-1f0a56921498", path: "/book-destination" },
    { name: "New Zealand", image: "https://images.unsplash.com/photo-1587613990441-f3c4e886d99a", path: "/book-destination" },
    { name: "Norway", image: "https://images.unsplash.com/photo-1507668077129-56e32842fceb", path: "/book-destination" },
    { name: "Italy", image: "https://images.unsplash.com/photo-1598970434795-0c54fe7c0647", path: "/book-destination" },
    { name: "Japan", image: "https://images.unsplash.com/photo-1551884170-f7b49983e8b2", path: "/book-destination" },
    { name: "Vietnam", image: "https://images.unsplash.com/photo-1564325724739-bae0bd08762c", path: "/book-destination" },
    { name: "Thailand", image: "https://images.unsplash.com/photo-1552925686-e8afeb4f6754", path: "/book-destination" },
    { name: "Dubai", image: "https://images.unsplash.com/photo-1512453979798-5ea266f8880c", path: "/book-destination" }
  ];

  const styles = {
    page: {
      fontFamily: '"Comic Sans MS", cursive, sans-serif',
      color: '#333',
      backgroundImage: 'url("https://images.unsplash.com/photo-1444703686981-a3abbc4d4fe3?q=80&w=1770&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      height: '100vh',
      overflow: 'auto',
      backdropFilter: 'blur(5px)' // Apply blur effect to the background
    },
    header: {
      background: 'rgba(0, 0, 0, 0.5)', // Slightly transparent
      padding: '1rem 2rem',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      position: 'fixed',
      width: '100%',
      top: 0,
      zIndex: 1000,
    },
    logo: {
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#fff',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '1rem',
      padding: '2rem',
      marginTop: '100px', // Adjusted for proper spacing from the header
    },
    card: {
      background: '#fff',
      border: '1px solid #ddd',
      borderRadius: '8px',
      overflow: 'hidden',
      cursor: 'pointer',
      transition: 'transform 0.3s ease',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.3)' // Added shadow for depth
    },
    image: {
      height: '200px',
      width: '100%',
      objectFit: 'cover', // Ensure the image fits well
    },
    title: {
      textAlign: 'center',
      padding: '20px',
      fontSize: '1.8rem',
      fontWeight: 'bold',
      color: '#fff', // White color for better visibility on blurred background
      textShadow: '0px 0px 8px rgba(0, 0, 0, 0.85)' // Text shadow for better legibility
    }
  };

  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <div style={styles.logo}>TravelMate</div>
      </header>
      <div style={styles.title}>Choose Your Destination</div>
      <div style={styles.grid}>
        {destinations.map((dest, index) => (
          <div key={index} style={styles.card} onClick={() => handleNavigate(dest.path)}>
            <img src={dest.image} alt={dest.name} style={styles.image} />
            <div style={{ ...styles.text, ...styles.title }}>{dest.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TourSelection;
