import React from 'react';
import { useNavigate } from 'react-router-dom';

function TourSelection() {
  const navigate = useNavigate();
  const destinations = [
    { name: "Indonesia", image: "https://link.to/indonesia.jpg", path: "/book-destination" },
    { name: "Switzerland", image: "https://link.to/switzerland.jpg", path: "/book-destination" },
    { name: "Iceland", image: "https://link.to/iceland.jpg", path: "/book-destination" },
    { name: "New Zealand", image: "https://link.to/newzealand.jpg", path: "/book-destination" },
    { name: "Norway", image: "https://link.to/norway.jpg", path: "/book-destination" },
    { name: "Italy", image: "https://link.to/italy.jpg", path: "/book-destination" },
    { name: "Japan", image: "https://link.to/japan.jpg", path: "/book-destination" },
    { name: "Vietnam", image: "https://link.to/vietnam.jpg", path: "/book-destination" },
    { name: "Thailand", image: "https://link.to/thailand.jpg", path: "/book-destination" },
    { name: "Dubai", image: "https://link.to/dubai.jpg", path: "/book-destination" }
  ];

  const handleNavigate = (path) => {
    navigate(path);
  };

  const styles = {
    page: {
      fontFamily: 'Roboto, sans-serif',
      color: '#333',
      padding: '60px 20px 20px 20px', // Adjust top padding to prevent overlap with the fixed header
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '1rem',
    },
    card: {
      cursor: 'pointer',
      overflow: 'hidden',
      position: 'relative',
      borderRadius: '8px',
      boxShadow: '0 4px 8px rgba(0,0,0,0.2)',
    },
    image: {
      width: '100%',
      height: '300px',
      objectFit: 'cover',
    },
    nameOverlay: {
      position: 'absolute',
      bottom: 0,
      left: 0,
      width: '100%',
      backgroundColor: 'rgba(0,0,0,0.6)',
      color: 'white',
      textAlign: 'center',
      padding: '10px 0',
      fontSize: '1.2rem',
      fontWeight: 'bold',
    }
  };

  return (
    <div style={styles.page}>
      <div style={styles.grid}>
        {destinations.map((dest, index) => (
          <div key={index} style={styles.card} onClick={() => handleNavigate(dest.path)}>
            <img src={dest.image} alt={dest.name} style={styles.image} />
            <div style={styles.nameOverlay}>{dest.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TourSelection;
