import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Payment.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlane } from '@fortawesome/free-solid-svg-icons';

function Payment() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: localStorage.getItem('userEmail') || '',
    contact: '',
    numberOfPeople: 1,
    totalCost: 2500.00
  });

  const [paymentProcessed, setPaymentProcessed] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "numberOfPeople") {
      const totalCost = Math.min(value, 10) * 2500;
      setFormData({ ...formData, numberOfPeople: value, totalCost });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handlePayment = () => {
    if (formData.name && formData.contact && formData.email && formData.numberOfPeople > 0) {
      setLoading(true);
      setTimeout(() => {
        setLoading(false);
        setPaymentProcessed(true);
        alert('Payment processed successfully. Please confirm your booking.');
      }, 2000);
    } else {
      alert('Please fill in all fields before processing payment.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!paymentProcessed) {
      alert('Please process the payment before confirming your booking.');
      return;
    }
    setLoading(true);
    try {
      const response = await fetch('/payment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          email: formData.email,
          packageSelected: localStorage.getItem('selectedPackage'),
          paymentStatus: 'Paid'
        })
      });
      if (response.ok) {
        setTimeout(() => {
          setLoading(false);
          navigate('/confirmation');
        }, 2000);
      } else {
        const errorData = await response.json();
        setTimeout(() => {
          setLoading(false);
          alert('Payment failed. Please try again. ' + errorData.message);
        }, 2000);
      }
    } catch (error) {
      setTimeout(() => {
        setLoading(false);
        alert('Error processing payment.');
      }, 2000);
    }
  };
  const styles = {
    contentAlignment: {
      textAlign: 'center',  
    },
    button: {
        display: 'block',
        width: '56%',
        padding: '15px',
        backgroundColor: 'rgb(53 236 168)',
        backgroundImage: 'linear-gradient(to right, rgb(51 76 255), rgb(255 45 45))',
        color: 'white',
        fontWeight: 'bold',
        textShadow: '1px 1px 2px black',
        border: 'none',
        borderRadius: '10px',
        cursor: 'pointer',
        fontSize: '1.2rem',
        textAlign: 'center',
        margin: '20px auto',
        boxShadow: '0 3px 6px rgba(0,0,0,0.16)',
        transition: 'all 0.3s ease',
        transform: 'translateY(0px)', // Initialize transform
      },
      loadingOverlay: {
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: '24px',
        color: '#000',
        zIndex: 1, // Ensure this is above all when loading
        backgroundColor: 'rgba(0, 0, 0, 0.5)', // Slight darkening
      },
      payButton:{
        backgroundColor: '#07e0d7',
        color: 'black',
      }
  };
  const hoverStyle = {
    ...styles.button,
    boxShadow: '0 5px 15px rgba(0,0,0,0.24)',
    backgroundImage: 'linear-gradient(to right, rgb(96 115 255), rgb(255 97 97))',
    transform: 'translateY(-2px)'
  };
  return (
    <div className="app-container">
      <header className="header">
        <a href="/" className="logo-link">
          <div className="logo"><FontAwesomeIcon icon={faPlane} /> TravelMate</div>
        </a>
      </header>
      {loading && (
        <div className="loading-overlay">
          <p>Loading...</p>
        </div>
      )}
      <div className="payment-container">
        <form onSubmit={handleSubmit} className="payment-form">
          <h1>Payment Details</h1>
          <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Your Name" required />
          <input type="email" name="email" value={formData.email} readOnly />
          <input type="text" name="contact" value={formData.contact} onChange={handleChange} placeholder="Your Contact Number" required />
          <select name="numberOfPeople" value={formData.numberOfPeople} onChange={handleChange}>
            {[...Array(10)].map((_, index) => (
              <option key={index} value={index + 1}>{index + 1}</option>
            ))}
          </select>
          <p>Total Cost: ${formData.totalCost.toFixed(2)}</p>
          <button type="button" onClick={handlePayment} disabled={!formData.name || !formData.contact || !formData.numberOfPeople}>
            Pay
          </button>
          <button type="submit" disabled={!paymentProcessed}>
            Confirm Payment
          </button>
        </form>
      </div>
    </div>
  );
}

export default Payment;
