import React, { useState, useEffect } from 'react';
import './TourBooking.css'; // Assuming you have a CSS file for styles

const TourBooking = () => {
    const [tours, setTours] = useState([]);
    const [selectedTour, setSelectedTour] = useState('');
    const [availableSlots, setAvailableSlots] = useState([]);
    const [selectedSlot, setSelectedSlot] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [participants, setParticipants] = useState(1);
    const [message, setMessage] = useState('');

    useEffect(() => {
        // Fetch available tours
        const fetchTours = async () => {
            const response = await fetch('/api/tours'); // Adjust API endpoint accordingly
            const data = await response.json();
            setTours(data);
        };
        fetchTours();
    }, []);

    const handleTourChange = (e) => {
        const tourId = e.target.value;
        setSelectedTour(tourId);

        // Fetch available slots for the selected tour
        const fetchSlots = async () => {
            const response = await fetch(`/api/tours/${tourId}/slots`);
            const slots = await response.json();
            setAvailableSlots(slots);
        };
        fetchSlots();
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const bookingData = { name, email, selectedSlot, participants };

        // Submit the booking
        const response = await fetch('/api/bookTour', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(bookingData),
        });

        if (response.ok) {
            setMessage('Booking successful!');
            // Reset form
            setName('');
            setEmail('');
            setSelectedSlot('');
            setParticipants(1);
        } else {
            setMessage('Booking failed. Please try again.');
        }
    };

    return (
        <div className="tour-booking">
            <h2>Book a Tour</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Select Tour:
                    <select value={selectedTour} onChange={handleTourChange} required>
                        <option value="">Select a tour</option>
                        {tours.map(tour => (
                            <option key={tour.id} value={tour.id}>{tour.name}</option>
                        ))}
                    </select>
                </label>

                {availableSlots.length > 0 && (
                    <label>
                        Available Slots:
                        <select value={selectedSlot} onChange={(e) => setSelectedSlot(e.target.value)} required>
                            <option value="">Select a slot</option>
                            {availableSlots.map(slot => (
                                <option key={slot.id} value={slot.time}>{slot.time}</option>
                            ))}
                        </select>
                    </label>
                )}

                <label>
                    Name:
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
                </label>

                <label>
                    Email:
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </label>

                <label>
                    Number of Participants:
                    <input type="number" value={participants} onChange={(e) => setParticipants(e.target.value)} min="1" required />
                </label>

                <button type="submit">Book Now</button>
            </form>

            {message && <p className={response.ok ? "message success" : "message"}>{message}</p>}
        </div>
    );
};

export default TourBooking;
