import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Payment() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: localStorage.getItem('userEmail') || '',
    contact: '',
    packageSelected: localStorage.getItem('selectedPackage') || '',
  });
  const [paid, setPaid] = useState(false); // To track if payment was made

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handlePayment = () => {
    setPaid(true);
    alert('Payment details added, click next to confirm booking.');
  };

  const handleConfirmBooking = async () => {
    const bookingData = {
      ...formData,
      paymentStatus: paid ? 'Paid' : 'Not Paid',
    };
    try {
      const response = await fetch('/payment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(bookingData)
      });
      if (response.ok) {
        navigate('/confirmation');
      } else {
        const errorData = await response.json();
        alert('Failed to confirm booking: ' + errorData.message);
      }
    } catch (error) {
      console.error('Error confirming booking:', error);
      alert('Error processing booking. Please try again later.');
    }
  };

  return (
    <div>
      <form onSubmit={(e) => e.preventDefault()}>
        <h1>Payment Details</h1>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Your Name"
          required
        />
        <input
          type="email"
          name="email"
          value={formData.email}
          readOnly // This field is pre-filled and not editable
        />
        <input
          type="text"
          name="contact"
          value={formData.contact}
          onChange={handleChange}
          placeholder="Your Contact Number"
          required
        />
        <p>Amount to Pay: $100</p> {/* Display fixed amount */}
        <button type="button" onClick={handlePayment} disabled={paid}>
          Pay
        </button>
        <button type="button" onClick={handleConfirmBooking}>
          Confirm Booking
        </button>
      </form>
    </div>
  );
}

export default Payment;
